document.addEventListener("alpine:init",function(){Alpine.store("adev_addresses",{selected:"",showPopup(e){return this.selected===e},edit(e){this.selected=e,document.querySelector('.adev-popup-wrapper[data-popup="adev-popup-'+e+'"]')&&document.querySelector("html").classList.add("adev-noscroll")},delete(e){confirm("Are you sure you wish to delete this address?")&&Shopify.postLink("/account/addresses/"+e,{parameters:{_method:"delete"}})},closePopup(){this.selected="",document.querySelector("html").classList.remove("adev-noscroll")},init(){}})});